    private void GuardarUsuario() {
        try {

            String nombre = txtNombre.getText();
            long idUsuario = Long.parseLong(txtIdUsuario.getText());//convertiendo a long de string 
            String direccion = txtDireccion.getText();
            String correo = txtCorreo.getText();
            int celular = Integer.parseInt(txtCelular.getText());
            String calificacion = txtCalificacion.getText();

            int repite = 0;
            if (centenela == 1) {//con centenela
                Usuario usuario = new Usuario(nombre, idUsuario, direccion, correo, celular, calificacion);
                listausuario.add(usuario);
                centenela++;
  
                JOptionPane.showMessageDialog(null, "SE GUARDO CORRECTAMENTE EL USUARIO");
            } else {

                for (int i = 0; i < listausuario.size(); i++) {
                    Usuario user = listausuario.get(i);
                    // Preguntamos si el usuario con id ya existe
                    if (!String.valueOf(idUsuario).equals(String.valueOf(user.getIdusuario()))) {
                        repite++;
                    }
                }
                if (repite == listausuario.size()) {
                    // Agregamos el Usuario en la lista
                    Usuario use = new Usuario(nombre, idUsuario, direccion, correo, celular, calificacion);
                    listausuario.add(use);
                }
            }

            //Limpiamos los campos
            txtNombre.setText("");
            txtIdUsuario.setText("");
            txtDireccion.setText("");
            txtCorreo.setText("");
            txtCelular.setText("");
            txtCalificacion.setText("");

            //cuando el usuario ingresa el id y la celular  mal     
        } catch (java.lang.NumberFormatException e) {
            JOptionPane.showMessageDialog(null, "Error,Ingreso un dato incorrecto!!!!! ", "MENSAJE", JOptionPane.WARNING_MESSAGE);

        }

    }
